import React, {useState} from 'react';
import './styles.css';
import register from '../../assets/register.jpeg';
import { useHistory } from 'react-router-dom';
import api from '../../services/api';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext } from 'pure-react-carousel';
import 'pure-react-carousel/dist/react-carousel.es.css';

export default function Register() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [whatsapp, setWhatsapp] = useState('');
  const [city, setCity] = useState('');
  const [uf, setUf] = useState('');
  const history = useHistory();
  const items = [
    {
        name: "Random Name #1",
        description: "Probably the most random thing you have ever seen!"
    },
    {
        name: "Random Name #2",
        description: "Hello World!"
    }
  ]
  async function handleregister(e) {
    e.preventDefault();
    const data = {
      name,
      email,
      whatsapp,
      city,
      uf
    }
    try {
      const response = await api.post('ongs', data);
      history.push('/');
      alert(`Seu ID de acesso ${response.data.id}`)
    } catch (err) {
      alert('Erro no cadastro, tente novamente');
    }

  }
  function Item(props)
  {
    return (
      <div>
        <TextField id="outlined-basic" label={props.name}  variant="outlined" />
      </div>
    )
  }
  return (
    <Grid container component="main">
      <Grid item xs={8} >
        <form onSubmit={handleregister} className="form-left">
          <div className="title"> Ol√°</div>
          <CarouselProvider
          naturalSlideWidth={200}
          naturalSlideHeight={70}
          totalSlides={items.length}
          dragEnabled={false}
          >
            <Slider>
            {
              items.map( (item, index) => {
                return  (<Slide index={index}>{Item(item)}</Slide>);
              })
            }
            </Slider>
            <ButtonBack>Back</ButtonBack>
            <ButtonNext>Next</ButtonNext>
          </CarouselProvider>       
        </form>
      </Grid>
      <Grid item xs={4} >
        <img src={register} alt="eventos online" className="image-right" />
      </Grid>
    </Grid>
  );
};
